insert.Script:
var d = new Date();

if (d.getMinutes() < 30)
    d.setMinutes(30,0,0);
else
    d.setMinutes(60,0,0);

$row.Date = d;

$row.Description = 'New Task';

if(!$row.StartDate){
  var start = new Date();
  start.setMinutes(0,0,0);
  start.setHours(start.getHours() + 1);
  
  var end = new Date(start);
  end.setHours(end.getHours() + 1);
  
  $row.StartDate = start;
  $row.EndDate = end;
}

